import React, { createContext, useState, useContext, useEffect, useCallback } from "react";
import { useAuth } from "./AuthContext";
import { fetchCart, addToCart, removeFromCart, increaseQuantity, decreaseQuantity } from "../services/CartService";
import axios from "axios";

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const { user } = useAuth();

  const loadCartFromServer = useCallback(async () => {
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    try {
      const cart = await fetchCart(userId); // Không cần token
      console.log("Cart from server:", cart);
      const itemsWithSelection = cart.items.map((item) => ({
        ...item,
        selected: item.selected || false,
      }));
      setCartItems(itemsWithSelection);
    } catch (error) {
      setCartItems([]);
    }
  }, [user]);

  const loadCartFromLocalStorage = useCallback(() => {
    try {
      const savedCart = localStorage.getItem("cartItems");
      setCartItems(savedCart ? JSON.parse(savedCart) : []);
    } catch (error) {
      console.error("Lỗi khi lấy giỏ hàng từ localStorage:", error);
      setCartItems([]);
    }
  }, []);

  useEffect(() => {
    if (user?._id) {
      loadCartFromServer();
    } else {
      console.warn("Không có user._id, sử dụng localStorage");
      loadCartFromLocalStorage();
    }
  }, [user, loadCartFromServer, loadCartFromLocalStorage]);

  useEffect(() => {
    if (!user?._id) {
      localStorage.setItem("cartItems", JSON.stringify(cartItems));
    }
  }, [cartItems, user]);

  const toggleCart = () => setIsOpen((prev) => !prev);

  const handleAddToCart = async (product) => {
    const { productId, quantity = 1, size, name, price, image } = product;
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";

    if (user?._id) {
      try {
        await addToCart(userId, { productId, quantity, size, name, price, image }); // Không cần token
        await loadCartFromServer();
      } catch (error) {
        // Lỗi đã được log trong CartService
      }
    } else {
      setCartItems((prev) => {
        const existingItem = prev.find((item) => item.productId === productId && item.size === size);
        if (existingItem) {
          return prev.map((item) =>
            item.productId === productId && item.size === size
              ? { ...item, quantity: item.quantity + quantity }
              : item
          );
        }
        return [...prev, { productId, quantity, size, name, price, image, selected: false }];
      });
    }
  };

  const handleRemoveFromCart = async (productId, size) => {
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    if (user?._id) {
      try {
        await removeFromCart(userId, productId, size); // Không cần token
        await loadCartFromServer();
      } catch (error) {
        // Lỗi đã được log trong CartService
      }
    } else {
      setCartItems((prev) =>
        prev.filter((item) => !(item.productId === productId && item.size === size))
      );
    }
  };

  const handleIncreaseQuantity = async (productId, size) => {
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    if (user?._id) {
      try {
        await increaseQuantity(userId, productId, size); // Không cần token
        await loadCartFromServer();
      } catch (error) {
        // Lỗi đã được log trong CartService
      }
    } else {
      setCartItems((prev) =>
        prev.map((item) =>
          item.productId === productId && item.size === size
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    }
  };

  const handleDecreaseQuantity = async (productId, size) => {
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    if (user?._id) {
      try {
        await decreaseQuantity(userId, productId, size); // Không cần token
        await loadCartFromServer();
      } catch (error) {
        // Lỗi đã được log trong CartService
      }
    } else {
      setCartItems((prev) =>
        prev
          .map((item) =>
            item.productId === productId && item.size === size && item.quantity > 1
              ? { ...item, quantity: item.quantity - 1 }
              : item
          )
          .filter((item) => item.quantity > 0)
      );
    }
  };

  const handleSelectAll = (isSelected) => {
    setCartItems((prev) =>
      prev.map((item) => ({
        ...item,
        selected: isSelected,
      }))
    );
    if (!user?._id) {
      localStorage.setItem(
        "cartItems",
        JSON.stringify(cartItems.map((item) => ({
          ...item,
          selected: isSelected,
        })))
      );
    }
  };

  const handleToggleItemSelection = (productId, size) => {
    setCartItems((prev) =>
      prev.map((item) =>
        item.productId === productId && item.size === size
          ? { ...item, selected: !item.selected }
          : item
      )
    );
    if (!user?._id) {
      localStorage.setItem(
        "cartItems",
        JSON.stringify(cartItems.map((item) =>
          item.productId === productId && item.size === size
            ? { ...item, selected: !item.selected }
            : item
        ))
      );
    }
  };

  const clearCart = async () => {
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    if (user?._id) {
      try {
        await axios.delete("http://localhost:5000/api/carts", {
          data: { userId },
        }); // Không cần token
        await loadCartFromServer();
      } catch (error) {
        console.error("Lỗi khi xóa giỏ hàng trên server:", error.response?.data || error.message);
      }
    } else {
      setCartItems([]);
      localStorage.setItem("cartItems", JSON.stringify([]));
    }
  };

  const total = cartItems
    .filter((item) => item.selected)
    .reduce((sum, item) => sum + item.price * item.quantity, 0);

  const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  const displaySize = (size) => {
    switch (size) {
      case "0.25":
        return "0.25kg";
      case "0.5":
        return "0.5kg";
      case "1":
        return "1kg";
      default:
        return size;
    }
  };

  return (
    <CartContext.Provider
      value={{
        cartItems,
        setCartItems,
        isOpen,
        toggleCart,
        total,
        totalItems,
        addToCart: handleAddToCart,
        removeFromCart: handleRemoveFromCart,
        increaseQuantity: handleIncreaseQuantity,
        decreaseQuantity: handleDecreaseQuantity,
        selectAll: handleSelectAll,
        toggleItemSelection: handleToggleItemSelection,
        fetchCartFromServer: loadCartFromServer,
        loadCartFromLocalStorage,
        clearCart,
        displaySize,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error("useCart must be used within a CartProvider");
  }
  return context;
};
