import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import "../styles/Payment.css";

const PaymentSuccess = () => {
  const { search } = useLocation();
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const params = new URLSearchParams(search);
  const txnRef = params.get("txnRef");
  const transactionNo = params.get("transactionNo");
  const transactionStatus = params.get("vnp_TransactionStatus");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const handlePaymentSuccess = async () => {
      if (!txnRef || !transactionNo || !transactionStatus) {
        setError("Thông tin giao dịch không hợp lệ. Đang chuyển hướng về trang chủ...");
        setTimeout(() => navigate("/"), 3000);
        return;
      }

      if (!user?.token) {
        setError("Vui lòng đăng nhập để tiếp tục.");
        setTimeout(() => navigate("/sign-in"), 3000);
        return;
      }

      if (transactionStatus !== "00") {
        setError("Giao dịch không thành công. Đang chuyển hướng...");
        setTimeout(() => navigate("/payment-failure"), 3000);
        return;
      }

      const pendingOrder = JSON.parse(localStorage.getItem("pendingOrder"));
      if (!pendingOrder) {
        setError("Không tìm thấy thông tin đơn hàng. Đang chuyển hướng...");
        setTimeout(() => navigate("/cart"), 3000);
        return;
      }

      const { items, total, address } = pendingOrder;

      try {
        const orderResponse = await axios.post(
          "http://localhost:5000/api/orders",
          { items, total, transactionId: transactionNo, address },
          { headers: { Authorization: `Bearer ${user.token}` } }
        );

        await axios.post(
          "http://localhost:5000/api/carts/remove-items",
          { items },
          { headers: { Authorization: `Bearer ${user.token}` } }
        );

        localStorage.removeItem("pendingOrder");
        setLoading(false);
      } catch (error) {
        if (error.response?.status === 401) {
          logout();
          setError("Phiên đăng nhập hết hạn. Vui lòng đăng nhập lại.");
          setTimeout(() => navigate("/sign-in"), 3000);
        } else {
          setError("Đã có lỗi xảy ra khi tạo đơn hàng. Vui lòng thử lại.");
          setTimeout(() => navigate("/payment-failure"), 3000);
        }
      }
    };

    handlePaymentSuccess();
  }, [txnRef, transactionNo, transactionStatus, user, navigate, logout]);

  if (loading) {
    return (
      <div className="payment-container">
        <h2>Đang xử lý thanh toán...</h2>
      </div>
    );
  }

  return (
    <div className="payment-container">
      <h2 className="success">Thanh toán thành công</h2>
      {error ? (
        <p className="error-message">{error}</p>
      ) : (
        <div className="order-summary">
          <h3>Thông tin giao dịch</h3>
          <p>Mã đơn hàng: <strong>{txnRef}</strong></p>
          <p>Mã giao dịch VNPay: <strong>{transactionNo}</strong></p>
          <p>Cảm ơn bạn đã mua sắm! Đơn hàng của bạn đã được ghi nhận.</p>
          <button onClick={() => navigate("/")} className="payment-btn">
            Quay lại trang chủ
          </button>
        </div>
      )}
    </div>
  );
};

export default PaymentSuccess;
