import React from 'react';

function Review() {
    const reviews = [
        { name: 'Mai Thái Huy', text: 'Món ăn rất ngon, không gian ấm cúng, nhân viên thân thiện!', image: '/images/khachhang1.jpg' },
        { name: 'Lưu Đức Quang', text: 'Tôi rất thích các món Thái ở đây, đặc biệt là Tom Yum!', image: '/images/khachhang2.jpg' },
        { name: 'Ma Thế Ngọc', text: 'Dịch vụ tuyệt vời, sẽ quay lại lần sau!', image: '/images/khachhang3.jpg' },
    ];

    return (
        <section className="review section-padding" id="feedback">
            <div className="container">
                <div className="row">
                    <div className="section-title" data-aos="fade-up-right" data-aos-duration="1500">
                        <h2 data-title="Phản hồi">Phản hồi từ khách hàng</h2>
                    </div>
                </div>
                <div className="row">
                    {reviews.map((review, index) => (
                        <div className="review-items" key={index} data-aos="zoom-in-up" data-aos-duration="1500">
                            <img src={review.image} alt={review.name} />
                            <div className="review-items-text">
                                <h2>{review.name}</h2>
                                <p>{review.text}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Review;
