// src/components/CartSidebar.jsx
import React from 'react';
import '../styles/cartsidebar.css';
import { useCart } from '../context/CartContext';

function CartSidebar() {
    const {
        cartItems,
        removeFromCart,
        isOpen,
        toggleCart,
        total,
        totalItems,
        increaseQuantity,
        decreaseQuantity,
        toggleItemSelection,
        selectAll,
        deselectAll,
    } = useCart();

    // Kiểm tra xem có bất kỳ món nào đang được chọn không
    const hasSelectedItems = cartItems.some(item => item.selected);

    const handleToggleAll = () => {
        if (hasSelectedItems) {
            deselectAll(); // Nếu có món đang được chọn, bỏ chọn tất cả
        } else {
            selectAll(); // Nếu không có món nào được chọn, chọn tất cả
        }
    };

    return (
        <>
            <div className="cart-toggle-btn" onClick={toggleCart}>
                <i className="fas fa-shopping-cart"></i>
                <span className="cart-count-badge">{totalItems}</span>
            </div>

            <div id="cart-sidebar" className={isOpen ? 'open' : 'closed'}>
                <div className="cart-header">
                    <h2>Giỏ hàng 🛒</h2>
                    <button className="close-btn" onClick={toggleCart}>
                        <i className="fas fa-times"></i>
                    </button>
                </div>
                <div className="cart-actions">
                    {cartItems.length > 0 && (
                        <button className="toggle-all-btn" onClick={handleToggleAll}>
                            <i className="fas fa-check"></i>
                        </button>
                    )}
                    <p id="cart-count">Tổng số lượng: {totalItems}</p>
                </div>
                <ul id="cart-items">
                    {cartItems.map((item, index) => (
                        <li key={index}>
                            <input
                                type="checkbox"
                                checked={item.selected}
                                onChange={() => toggleItemSelection(index)}
                                className="cart-item-checkbox"
                            />
                            <img src={item.image} alt={item.name} className="cart-item-image" />
                            <div className="cart-item-details">
                                <span>{item.name}</span>
                                <span>{item.price.toLocaleString()} VND</span>
                                <div className="quantity-controls">
                                    <button onClick={() => decreaseQuantity(index)}>-</button>
                                    <span>{item.quantity}</span>
                                    <button onClick={() => increaseQuantity(index)}>+</button>
                                </div>
                            </div>
                            <button onClick={() => removeFromCart(index)}>Xóa</button>
                        </li>
                    ))}
                </ul>
                <p>Tổng tiền (đã chọn): <span id="cart-total">{total.toLocaleString()} VND</span></p>
                <button id="checkout-btn" onClick={() => window.location.href = 'https://your-payment-link.com'}>
                    Thanh toán
                </button>
            </div>
        </>
    );
}

export default CartSidebar;
