// src/components/Header.jsx
import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';
import '../styles/Header.css';
import { useAuth } from '../context/AuthContext'; // Import useAuth

function Header() {
    const [menuOpen, setMenuOpen] = useState(false);
    const { user, logout } = useAuth(); // Lấy user và logout từ context

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <header className="header">
            <div className="container">
                <div className="header-content">
                    {/* Logo */}
                    <div className="logo">
                        <Link to="/">
                            <img
                                src="/images/TMKLogo2020Xanh.png"
                                alt="Thai Market Logo"
                                onError={(e) => (e.target.src = 'https://via.placeholder.com/150')}
                            />
                        </Link>
                    </div>

                    {/* Navigation */}
                    <nav className={`nav-menu ${menuOpen ? 'open' : ''}`}>
                        <ul style={{ justifyContent: 'flex-start' }}> {/* Đưa menu về bên trái */}
                            <li>
                                <NavLink to="/" end className={({ isActive }) => (isActive ? 'active' : '')}>
                                    Trang Chủ
                                </NavLink>
                            </li>
                        </ul>
                    </nav>

                    {/* User Actions */}
                    <div className="user-actions">
                        {user ? (
                            <div className="user-greeting">
                                <span>Chào {user.username}</span>
                                <button onClick={logout} className="sign-out-btn">
                                    Đăng Xuất
                                </button>
                            </div>
                        ) : (
                            <Link to="/sign-in" className="sign-in-btn">
                                Đăng Nhập
                            </Link>
                        )}
                        <button className="menu-toggle" onClick={toggleMenu}>
                            <i className="fas fa-bars"></i>
                        </button>
                    </div>
                </div>
            </div>
        </header>
    );
}

export default Header;
