import React, { useEffect, useState } from "react";
import { useCart } from "../context/CartContext";
import { useLocation, useNavigate } from "react-router-dom";
import "../styles/Payment.css";

function Payment() {
  const { cartItems } = useCart();
  const location = useLocation();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);

  const selectedItems = location.state?.item
    ? [location.state.item]
    : cartItems.filter((item) => item.selected);

  const paymentTotal = selectedItems.reduce(
    (sum, item) => sum + (item.price || 0) * (item.quantity || 1),
    0
  );

  useEffect(() => {
    if (selectedItems.length === 0) {
      navigate("/cart");
    }
  }, [selectedItems, navigate]);

  const handlePayment = async () => {
    if (selectedItems.length === 0 || paymentTotal <= 0) {
      alert("Không có sản phẩm nào để thanh toán!");
      return;
    }

    setIsLoading(true);
    try {
      const orderInfo = selectedItems
        .map(
          (item) =>
            `${item.name || "Sản phẩm"} (${item.size || "0"}kg) - ${
              item.quantity || 1
            } x ${(item.price || 0).toLocaleString()} VND`
        )
        .join("; ");

      const response = await fetch(
        `http://localhost:5000/create_payment?amount=${paymentTotal}&orderInfo=${encodeURIComponent(
          orderInfo
        )}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      const data = await response.json();

      if (response.ok && data.status === "success" && data.url) {
        window.location.href = data.url;
      } else {
        throw new Error(data.error || "Không nhận được URL thanh toán");
      }
    } catch (error) {
      console.error("Lỗi khi tạo thanh toán:", error);
      alert(`Có lỗi xảy ra: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="payment-container">
      <h2>Thanh toán</h2>
      {selectedItems.length > 0 ? (
        <>
          <div className="payment-items">
            {selectedItems.map((item, index) => (
              <div key={index} className="payment-item">
                <img
                  src={item.image || ""}
                  alt={item.name || "Sản phẩm"}
                  className="payment-item-image"
                />
                <div className="payment-item-details">
                  <p>
                    {item.name || "Sản phẩm không xác định"} (
                    {item.size || "0"}kg)
                  </p>
                  <p>Đơn giá: {(item.price || 0).toLocaleString()} VND</p>
                  <p>Số lượng: {item.quantity || 1}</p>
                  <p>
                    Tổng:{" "}
                    {((item.price || 0) * (item.quantity || 1)).toLocaleString()}{" "}
                    VND
                  </p>
                </div>
              </div>
            ))}
          </div>
          <div className="payment-summary">
            <p>
              Tổng cộng ({selectedItems.length} sản phẩm):{" "}
              <strong>{paymentTotal.toLocaleString()} VND</strong>
            </p>
            <button
              className="payment-btn"
              onClick={handlePayment}
              disabled={isLoading}
            >
              {isLoading ? "Đang xử lý..." : "Thanh toán bằng VNPay"}
            </button>
          </div>
        </>
      ) : (
        <p>Không có sản phẩm nào để thanh toán.</p>
      )}
    </div>
  );
}

export default Payment;
