import React, { useState } from 'react';
import { useNavigate } from ' EmeryBoard-dom';
import '../styles/sign-up.css';
import { registerUser } from '../services/signupService';
import { useAuth } from '../context/AuthContext';

function SignUp() {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        phone: '',
        password: '',
    });

    const [message, setMessage] = useState('');
    const navigate = useNavigate();
    const { login } = useAuth();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const result = await registerUser(formData); // Gọi API đăng ký
            setMessage(result.message);

            // Giả sử server trả về token sau khi đăng ký
            const { token } = result; // Cập nhật theo response thực tế từ registerUser
            if (token) {
                localStorage.setItem('token', token); // Lưu token
                login({ username: formData.username, email: formData.email }); // Gọi login với dữ liệu phù hợp
            }

            setFormData({ username: '', email: '', phone: '', password: '' });
            setTimeout(() => {
                navigate('/sign-in');
            }, 2000);
        } catch (error) {
            setMessage(error.message || 'Đăng ký thất bại');
        }
    };

    return (
        <div className="sign-up">
            <h1 className="sign-up-heading">Đăng kí</h1>
            {message && (
                <p style={{ color: message.includes('thành công') ? 'green' : 'red', textAlign: 'center' }}>
                    {message}
                </p>
            )}
            <form id="signUpForm" className="sign-up-form" onSubmit={handleSubmit}>
                <label htmlFor="username" className="sign-up-label">Họ và tên</label>
                <input
                    id="username"
                    name="username"
                    className="sign-up-input"
                    placeholder="Ví dụ: Mai Thái Huy"
                    value={formData.username}
                    onChange={handleChange}
                    required
                />
                <label htmlFor="email" className="sign-up-label">Email</label>
                <input
                    id="email"
                    name="email"
                    className="sign-up-input"
                    type="email"
                    placeholder="Ví dụ: abcxyz@gmail.com"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
                <label htmlFor="phone" className="sign-up-label">Số Điện Thoại</label>
                <input
                    id="phone"
                    name="phone"
                    className="sign-up-input"
                    type="tel"
                    placeholder="Ví dụ: 0123456789"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                />
                <label htmlFor="password" className="sign-up-label">Mật Khẩu</label>
                <input
                    id="password"
                    name="password"
                    className="sign-up-input"
                    type="password"
                    placeholder="Nhập mật khẩu của bạn"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
                <button type="submit" className="sign-up-submit">Đăng kí</button>
            </form>
            <p className="sign-up-already">
                <span>Bạn đã có tài khoản?</span>
                <a href="/sign-in" className="sign-up-login">Đăng nhập</a>
            </p>
        </div>
    );
}

export default SignUp;
