import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import BigImage from './components/BigImage';
import About from './components/About';
import Menu from './components/Menu';
import CartSidebar from './components/CartSidebar';
import Team from './components/Team';
import Home from './components/Home'; // Giả định bạn có component Home
import Products from './components/Products'; // Giả định bạn có component Products
import Contact from './components/Contact'; // Giả định bạn có component Contact
import SignIn from './components/SignIn'; // Giả định bạn có component SignIn
import Cart from './components/Cart'; // Giả định bạn có component Cart

function App() {
    // State để quản lý giỏ hàng
    const [cartItems, setCartItems] = useState([]);

    // Hàm thêm vào giỏ hàng
    const addToCart = (name, price, image) => {
        const newItem = { name, price, image };
        setCartItems([...cartItems, newItem]);
    };

    // Hàm xóa món khỏi giỏ hàng
    const removeFromCart = (index) => {
        const updatedCart = cartItems.filter((_, i) => i !== index);
        setCartItems(updatedCart);
    };

    return (
        <>
            {/* Header và CartSidebar luôn hiển thị trên tất cả các trang */}
            <Header />
            <CartSidebar cartItems={cartItems} removeFromCart={removeFromCart} />

            {/* Các route */}
            <Routes>
                <Route
                    path="/"
                    element={
                        <>
                            <BigImage />
                            <About />
                            <Menu addToCart={addToCart} />
                            <Team />
                        </>
                    }
                />
                <Route path="/products" element={<Products addToCart={addToCart} />} />
                <Route path="/about" element={<About />} />
                <Route path="/contact" element={<Contact />} />
                <Route path="/sign-in" element={<SignIn />} />
                <Route path="/cart" element={<Cart cartItems={cartItems} removeFromCart={removeFromCart} />} />
            </Routes>
        </>
    );
}

export default App;
