import React, { useEffect, useState } from 'react';
import { useCart } from '../context/CartContext';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/Payment.css';

function Payment() {
    const { cartItems } = useCart();
    const location = useLocation();
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(false);

    // Lấy danh sách sản phẩm được chọn
    const selectedItems = location.state?.item 
        ? [location.state.item]
        : cartItems.filter(item => item.selected);

    // Kiểm tra và log selectedItems
    console.log('Selected Items:', selectedItems);

    // Tính tổng tiền với kiểm tra nghiêm ngặt
    const paymentTotal = selectedItems.reduce((sum, item) => {
        const price = item.price || 0;
        const quantity = item.quantity || 1;
        console.log(`Item: ${item.name || 'Unknown'}, Price: ${price}, Quantity: ${quantity}`);
        return sum + (price * quantity);
    }, 0);

    console.log('Payment Total:', paymentTotal);

    useEffect(() => {
        if (selectedItems.length === 0) {
            navigate('/cart');
        }
    }, [selectedItems, navigate]);

    const handlePayment = async () => {
        if (selectedItems.length === 0 || paymentTotal <= 0) {
            alert('Không có sản phẩm nào để thanh toán hoặc tổng tiền không hợp lệ!');
            navigate('/cart');
            return;
        }

        setIsLoading(true);
        try {
            // Tạo orderInfo với kiểm tra nghiêm ngặt
            const orderInfo = selectedItems.map(item => {
                const name = item.name || 'Sản phẩm không xác định';
                const size = item.size || '0';
                const quantity = item.quantity || 1;
                const price = item.price || 0;
                return `${name} (${size}kg) - ${quantity} x ${price.toLocaleString()} VND`;
            }).join('; ');

            const encodedOrderInfo = encodeURIComponent(orderInfo);

            console.log('Order Info:', orderInfo);
            console.log('Encoded Order Info:', encodedOrderInfo);
            console.log('Amount Sent:', paymentTotal);

            const response = await fetch(`http://localhost:5000/create_payment?amount=${paymentTotal}&orderInfo=${encodedOrderInfo}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            const data = await response.json();

            console.log('Response từ server:', data);

            if (response.ok && data.status === 'success' && data.url) {
                window.location.href = data.url;
            } else {
                throw new Error(data.error || 'Không nhận được URL thanh toán hợp lệ.');
            }
        } catch (error) {
            console.error('Lỗi khi tạo thanh toán:', error);
            alert(`Có lỗi xảy ra: ${error.message}`);
            setIsLoading(false);
        }
    };

    return (
        <div className="payment-container">
            <h2>Thanh toán</h2>
            {selectedItems.length > 0 ? (
                <>
                    <div className="payment-items">
                        {selectedItems.map((item, index) => (
                            <div key={index} className="payment-item">
                                <img src={item.image || ''} alt={item.name || 'Sản phẩm'} className="payment-item-image" />
                                <div className="payment-item-details">
                                    <p>{item.name || 'Sản phẩm không xác định'} ({item.size || '0'}kg)</p>
                                    <p>Đơn giá: {(item.price || 0).toLocaleString()} VND</p>
                                    <p>Số lượng: {item.quantity || 1}</p>
                                    <p>Tổng: {((item.price || 0) * (item.quantity || 1)).toLocaleString()} VND</p>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="payment-summary">
                        <p>Tổng cộng ({selectedItems.length} sản phẩm): <strong>{paymentTotal.toLocaleString()} VND</strong></p>
                        <button 
                            className="payment-btn" 
                            onClick={handlePayment}
                            disabled={isLoading}
                        >
                            {isLoading ? 'Đang xử lý...' : 'Thanh toán bằng VNPay'}
                        </button>
                    </div>
                </>
            ) : (
                <p>Không có sản phẩm nào để thanh toán.</p>
            )}
        </div>
    );
}

export default Payment;
