export async function fetchMenuItems(category) {
    try {
        const response = await fetch(`http://localhost:5000/api/products/list?category=${category}`);
        if (!response.ok) throw new Error("Lỗi khi lấy dữ liệu menu");
        return await response.json();
    } catch (error) {
        console.error(error);
        return [];
    }
}
