import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        const savedCart = localStorage.getItem('cartItems');
        try {
            const parsedCart = savedCart ? JSON.parse(savedCart) : [];
            // Chuẩn hóa dữ liệu từ localStorage
            return Array.isArray(parsedCart) ? parsedCart.map(item => ({
                name: String(item.name || ''),
                price: Number(item.price || 0),
                image: String(item.image || ''),
                weight: String(item.weight || '25'), // Mặc định là "25" nếu không hợp lệ
                quantity: Number(item.quantity || 1),
                selected: Boolean(item.selected ?? true)
            })) : [];
        } catch (error) {
            console.error("Lỗi khi parse cartItems từ localStorage:", error);
            return [];
        }
    });

    const [isOpen, setIsOpen] = useState(false);

    const addToCart = (item) => {
        setCartItems(prev => {
            const existingItem = prev.find(i => i.name === item.name && i.weight === item.weight);
            let updatedCart;
            if (existingItem) {
                updatedCart = prev.map(i =>
                    i.name === item.name && i.weight === item.weight
                        ? { ...i, quantity: i.quantity + item.quantity }
                        : i
                );
            } else {
                updatedCart = [...prev, { ...item, selected: true }];
            }
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
        setIsOpen(true);
    };

    const removeFromCart = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.filter((_, i) => i !== index);
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const toggleCart = () => {
        setIsOpen(prev => !prev);
    };

    const increaseQuantity = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index ? { ...item, quantity: item.quantity + 1 } : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const decreaseQuantity = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index ? { ...item, quantity: Math.max(1, item.quantity - 1) } : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const toggleItemSelection = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index ? { ...item, selected: !item.selected } : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const selectAll = () => {
        setCartItems(prev => {
            const updatedCart = prev.map(item => ({ ...item, selected: true }));
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const deselectAll = () => {
        setCartItems(prev => {
            const updatedCart = prev.map(item => ({ ...item, selected: false }));
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
    const total = cartItems.reduce(
        (sum, item) => (item.selected ? sum + item.price * item.quantity : sum),
        0
    );

    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    return (
        <CartContext.Provider
            value={{
                cartItems,
                addToCart,
                removeFromCart,
                isOpen,
                toggleCart,
                total,
                totalItems,
                increaseQuantity,
                decreaseQuantity,
                toggleItemSelection,
                selectAll,
                deselectAll,
            }}
        >
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
