import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import AddressService from '../services/AddressService';
import ShippingAddressForm from './ShippingAddressForm';
import "../styles/RelatedItem.css";

const RelatedItems = ({ relatedProducts, currentProductId, addToCart }) => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [addresses, setAddresses] = useState([]);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [isAddressFormOpen, setIsAddressFormOpen] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Lấy danh sách địa chỉ từ database khi component mount
  useEffect(() => {
    const fetchAddresses = async () => {
      if (!user || !user._id) {
        setError('Vui lòng đăng nhập để mua hàng.');
        navigate('/sign-in');
        return;
      }

      const token = localStorage.getItem('token');
      if (!token) {
        setError('Không tìm thấy token. Vui lòng đăng nhập lại.');
        logout();
        navigate('/sign-in');
        return;
      }

      setLoading(true);
      try {
        const addressList = await AddressService.getAddresses();
        setAddresses(addressList || []);
        const defaultAddress = await AddressService.getDefaultAddress();
        if (defaultAddress) {
          setSelectedAddress({
            fullName: defaultAddress.fullName,
            phone: defaultAddress.phone,
            address: `${defaultAddress.detail}, ${defaultAddress.ward}, ${defaultAddress.district}, ${defaultAddress.province}`,
          });
        }
      } catch (error) {
        console.error('Lỗi khi lấy địa chỉ:', error);
        if (error.message === 'Token không tồn tại. Vui lòng đăng nhập lại.') {
          setError('Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.');
          logout();
          navigate('/sign-in');
        } else {
          setError(`Không thể tải danh sách địa chỉ: ${error.message}`);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchAddresses();
  }, [user, logout, navigate]);

  // Chọn địa chỉ từ danh sách
  const handleAddressSelect = (address) => {
    setSelectedAddress({
      fullName: address.fullName,
      phone: address.phone,
      address: `${address.detail}, ${address.ward}, ${address.district}, ${address.province}`,
    });
  };

  // Xử lý khi thêm địa chỉ mới
  const handleNewAddressAdded = async (newAddress) => {
    setSelectedAddress({
      fullName: newAddress.fullName,
      phone: newAddress.phone,
      address: `${newAddress.detail}, ${newAddress.ward}, ${newAddress.district}, ${newAddress.province}`,
    });
    try {
      const updatedAddresses = await AddressService.getAddresses();
      setAddresses(updatedAddresses || []);
    } catch (error) {
      setError(`Không thể cập nhật danh sách địa chỉ: ${error.message}`);
    }
    setIsAddressFormOpen(false);
  };

  // Xử lý khi nhấn "Mua ngay"
  const handleBuyNow = (product) => {
    const price = product.prices?.["0"]?.["25"]; // Giá cho size 0.25kg
    if (!price) {
      console.error("Lỗi: Không tìm thấy giá sản phẩm", product);
      setError("Không tìm thấy giá sản phẩm.");
      return;
    }

    if (!user || !user._id) {
      setError("Vui lòng đăng nhập để tiếp tục mua hàng.");
      navigate('/sign-in');
      return;
    }

    if (!selectedAddress && addresses.length === 0) {
      setError("Vui lòng thêm địa chỉ giao hàng trước khi mua.");
      setIsAddressFormOpen(true); // Mở form để thêm địa chỉ
      return;
    }

    const item = {
      productId: product._id,
      name: product.name,
      price: price,
      image: product.image,
      size: "0.25",
      quantity: 1,
      selected: true, // Đánh dấu là đã chọn
    };

    navigate("/payment", {
      state: {
        selectedItems: [item],
        total: price * 1,
        selectedAddress: selectedAddress,
      },
    });
  };

  return (
    <div className="related-items">
      <h3>Sản phẩm cùng loại</h3>
      {loading && <p>Đang tải dữ liệu...</p>}
      {error && <p className="error-message">{error}</p>}
      <div className="address-section">
        <h4>Địa chỉ giao hàng</h4>
        {addresses.length === 0 ? (
          <div>
            <p>Chưa có địa chỉ giao hàng.</p>
            <button
              onClick={() => setIsAddressFormOpen(true)}
              className="add-address-btn"
            >
              Thêm địa chỉ giao hàng
            </button>
          </div>
        ) : (
          <div className="address-list">
            {selectedAddress ? (
              <div className="selected-address">
                <p>
                  <strong>{selectedAddress.fullName}</strong> ({selectedAddress.phone})
                </p>
                <p>{selectedAddress.address}</p>
                <button onClick={() => setIsAddressFormOpen(true)} className="change-address-btn">
                  Thay đổi
                </button>
              </div>
            ) : (
              <p>Chưa chọn địa chỉ giao hàng.</p>
            )}
            {!isAddressFormOpen && (
              <div className="address-options">
                <h5>Chọn địa chỉ:</h5>
                {addresses.map((address) => (
                  <div key={address._id} className="address-item">
                    <div onClick={() => handleAddressSelect(address)} className="address-content">
                      <p>
                        <strong>{address.fullName}</strong> ({address.phone})
                      </p>
                      <p>{`${address.detail}, ${address.ward}, ${address.district}, ${address.province}`}</p>
                    </div>
                  </div>
                ))}
                <button
                  onClick={() => setIsAddressFormOpen(true)}
                  className="add-address-btn"
                >
                  Thêm địa chỉ mới
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      <div className="related-list">
        {relatedProducts.length > 0 ? (
          relatedProducts
            .filter(product => product._id !== currentProductId)
            .map(product => {
              const currentPrice = product.prices?.["0"]?.["25"];

              return (
                <div key={product._id} className="related-item">
                  <div
                    onClick={() => navigate(`/${product.category}/${product._id}`)}
                    style={{ cursor: 'pointer' }}
                  >
                    <img src={product.image} alt={product.name} className="related-image" />
                    <h4 className="related-name">{product.name}</h4>
                    <p className="related-price">
                      {currentPrice
                        ? `${currentPrice.toLocaleString()} VND / 0.25kg`
                        : "Giá chưa cập nhật"}
                    </p>
                  </div>
                  <div className="related-actions">
                    <button
                      className="btn-add"
                      onClick={(e) => {
                        e.stopPropagation();
                        addToCart({
                          productId: product._id,
                          name: product.name,
                          price: currentPrice,
                          image: product.image,
                          size: "0.25",
                          quantity: 1,
                        });
                      }}
                    >
                      🛒
                    </button>
                    <button
                      className="btn-buy"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleBuyNow(product);
                      }}
                      disabled={loading}
                    >
                      Mua ngay
                    </button>
                  </div>
                </div>
              );
            })
        ) : (
          <p>Không có sản phẩm liên quan.</p>
        )}
      </div>

      {/* Modal để thêm địa chỉ */}
      {isAddressFormOpen && (
        <div className="address-form-overlay">
          <ShippingAddressForm
            onAddressSelect={handleNewAddressAdded}
            onClose={() => setIsAddressFormOpen(false)}
          />
        </div>
      )}
    </div>
  );
};

export default RelatedItems;
