import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import AddressService from "../services/AddressService";

const Product = ({ product }) => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [selectedWeight, setSelectedWeight] = useState("25");
  const [quantity, setQuantity] = useState(1);

  const getPriceFromSchema = (weight) => {
    const prices = { "25": 100000, "5": 180000, "1": 350000 }; // Giả lập
    return prices[weight];
  };

  const handleBuyNow = async () => {
    const price = getPriceFromSchema(selectedWeight);
    if (price === undefined) {
      console.error("Lỗi: Không tìm thấy giá sản phẩm", product);
      return;
    }

    const size = selectedWeight === "25" ? "0.25" : selectedWeight === "5" ? "0.5" : "1";
    const totalAmount = price * quantity;

    const item = {
      productId: product._id,
      name: product.name,
      price: price,
      image: product.image,
      size: size,
      quantity,
    };

    let selectedAddress = null;
    if (user && user._id) {
      try {
        const addresses = await AddressService.getAddresses(user._id); // Giả định API lấy địa chỉ
        selectedAddress = addresses.find((addr) => addr.isDefault) || addresses[0] || null;
      } catch (error) {
        console.error("Lỗi khi lấy địa chỉ:", error);
      }
    }

    navigate("/payment", {
      state: {
        selectedItems: [item],
        total: totalAmount,
        selectedAddress: selectedAddress
          ? {
              fullName: selectedAddress.fullName,
              address: `${selectedAddress.detail}, ${selectedAddress.ward}, ${selectedAddress.district}, ${selectedAddress.province}`,
              phone: selectedAddress.phone,
            }
          : null,
      },
    });
  };

  return (
    <div className="product-item">
      <div className="product-image-container">
        <img src={product.image} alt={product.name} className="product-image" />
      </div>
      <div className="product-info">
        <h1 className="product-name">{product.name}</h1>
        <p className="product-description">{product.description}</p>
        <div className="product-price">
          <span className="current-price">{getPriceFromSchema(selectedWeight).toLocaleString()}đ</span>
        </div>
        <div className="product-options">
          <label>Trọng lượng:</label>
          <div className="weight-options">
            {["25", "5", "1"].map((weight) => (
              <div
                key={weight}
                className={`weight-option ${selectedWeight === weight ? "selected" : ""}`}
                onClick={() => setSelectedWeight(weight)}
              >
                {weight === "25" ? "250g" : weight === "5" ? "500g" : "1kg"}
              </div>
            ))}
          </div>
        </div>
        <div className="product-quantity">
          <label>Số lượng:</label>
          <div className="quantity-controls">
            <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>-</button>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(Math.max(1, e.target.value))}
            />
            <button onClick={() => setQuantity(quantity + 1)}>+</button>
          </div>
        </div>
        <div className="product-actions">
          <button className="btn-add">Thêm vào giỏ</button>
          <button className="btn-buy" onClick={handleBuyNow}>
            Mua ngay
          </button>
        </div>
      </div>
    </div>
  );
};

export default Product;
