import React, { useState } from 'react';
import "../styles/ProductItem.css";

const ProductItem = ({ product, addToCart }) => {
    const [selectedWeight, setSelectedWeight] = useState('25');
    const [quantity, setQuantity] = useState(1);

    const getPriceFromSchema = (weight) => {
        if (weight === "1") {
            return product.prices?.["1"];
        } else {
            return product.prices?.["0"]?.[weight];
        }
    };

    const handleAddToCart = () => {
        const price = getPriceFromSchema(selectedWeight);
        if (price === undefined) {
            console.error("Lỗi: Không tìm thấy giá sản phẩm", product);
            return;
        }

        const size = selectedWeight === "25" ? "0.25" : selectedWeight === "5" ? "0.5" : "1";

        addToCart({ 
            productId: product._id,
            name: product.name, 
            price: price, 
            image: product.image,
            size: size,
            quantity
        });
    };

    return (
        <div className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <h2 className="product-name">{product.name}</h2>
            <p className="product-description">{product.description}</p>
            <div className="product-meta">
                <span className="product-rating">⭐ {product.rating} ({product.sold} lượt bán)</span>
            </div>
            <div className="product-price">
                Giá: {getPriceFromSchema(selectedWeight) 
                    ? `${getPriceFromSchema(selectedWeight).toLocaleString()} VND`
                    : "Giá chưa cập nhật"} 
                / {selectedWeight === "25" ? "0.25kg" : selectedWeight === "5" ? "0.5kg" : "1kg"}
            </div>
            <div className="product-options">
                <label>Chọn trọng lượng: </label>
                <select value={selectedWeight} onChange={(e) => setSelectedWeight(e.target.value)}>
                    <option value="25">0.25kg</option>
                    <option value="5">0.5kg</option>
                    <option value="1">1kg</option>
                </select>
            </div>
            <div className="product-quantity">
                <label>Số lượng: </label>
                <input 
                    type="number" 
                    min="1" 
                    value={quantity} 
                    onChange={(e) => setQuantity(Math.max(1, Number(e.target.value)))}
                />
            </div>
            <div className="product-actions">
                <button onClick={handleAddToCart} className="btn-add">Thêm vào giỏ hàng</button>
                <button className="btn-buy">Mua ngay</button>
            </div>
        </div>
    );
};

export default ProductItem;
